/* Demo for FinTech@SG Course 
Generation of Charts Based on JSON data from Server
Author: Prof Bhojan Anand */
//Install d3.js:   npm install d3 --save
import React from "react";
import logo from "./brand-logo.png";
import * as d3 from 'd3' 

import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      accountsData: [],
      customersData: [],
      transactionsData: []
    };
  }



  callAPIServerAccounts() {
    // when button clicked, start a GET request
    // to specified URL
    fetch("https://f92c6b35-d121-4c8a-987b-81217155297f.mock.pstmn.io/accounts")
      .then(res => res.text())
      .then(res => this.setState({ accountsData: JSON.parse(res) }))
      .catch(err => err);

    }

  callAPIServerCustomers() {
      // when button clicked, start a GET request
      // to specified URL
      fetch("https://f92c6b35-d121-4c8a-987b-81217155297f.mock.pstmn.io/customers")
        .then(res => res.text())
        .then(res => this.setState({ customersData: JSON.parse(res) }))
        .catch(err => err);

      }

    callAPIServerTransactions() {
        // when button clicked, start a GET request
        // to specified URL
        fetch("https://f92c6b35-d121-4c8a-987b-81217155297f.mock.pstmn.io/transactions")
          .then(res => res.text())
          .then(res => this.setState({ transactionsData: JSON.parse(res) }))
          .catch(err => err);

        }

  accountsButton() {
    this.callAPIServerAccounts();
  }


  customersButton() {
    this.callAPIServerCustomers();
  }

  transactionsButton() {
    this.callAPIServerTransactions();


  }

  componentDidUpdate(prevProps, prevState) {
  //when data received run this
    /* prepare data */
    if (prevState.accountsData !== this.state.accountsData){
    this.state.accountsData.forEach(function (d) {
      d.balance = d.balance.replace(/[^0-9.-]+/g,"");  //regular expression to convert currency to Numeric form
    });

    this.showChartAccounts();  //improved version way to chart
  }
  else if (prevState.transactionsData !== this.state.transactionsData){
    this.state.transactionsData.forEach(function (d) {
      d.amount = d.amount.replace(/[^0-9.-]+/g,"");  //regular expression to convert currency to Numeric form
    });
    this.showChartTransactions();
  }}



  showChartAccounts() {


    const margin = { top: 50, right: 50, bottom: 50, left: 50 };
    var width = 1000 - margin.left - margin.right;
    var height = 500 - margin.top - margin.bottom;
    var svg = d3.select("#barChart")
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom)
      .append("g")
      .attr("transform", "translate(" + margin.left + "," + margin.top + ")")

    svg.selectAll("*").remove();

    var x = d3.scaleBand().range([0, width]);
    var y = d3.scaleLinear().range([height, 0]);

    x.domain(this.state.accountsData.map(details => details.account));
    y.domain([0, d3.max(this.state.accountsData.map(details => details.balance))]);



    svg.selectAll(".bar")
      .data(this.state.accountsData)
      .enter()
      .append("rect")
      .attr("class", "bar")
      .attr("x", d => x(d.account))
      .attr("width", x.bandwidth() - 10)
      .attr("y", d => y(d.balance))
      .attr("height", d => height - y(d.balance));

    svg.append("g")
      .attr("transform", "translate(0," + height + ")")
      .call(d3.axisBottom(x))

    svg.append("g")
      .call(d3.axisLeft(y));
  }

  showChartTransactions() {


    const margin = { top: 50, right: 50, bottom: 50, left: 50 };
    var width = 1000 - margin.left - margin.right;
    var height = 500 - margin.top - margin.bottom;
    var svg = d3.select("#barChart2")
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom)
      .append("g")
      .attr("transform", "translate(" + margin.left + "," + margin.top + ")")

    svg.selectAll("*").remove();

    var x = d3.scaleBand().range([0, width]);
    var y = d3.scaleLinear().range([height, 0]);

    x.domain(this.state.transactionsData.map(details => details.category));
    y.domain([0, d3.max(this.state.transactionsData.map(details => details.amount))]);



    svg.selectAll(".bar")
      .data(this.state.transactionsData)
      .enter()
      .append("rect")
      .attr("class", "bar")
      .attr("x", d => x(d.category))
      .attr("width", x.bandwidth() - 10)
      .attr("y", d => y(d.amount))
      .attr("height", d => height - y(d.amount));

    svg.append("g")
      .attr("transform", "translate(0," + height + ")")
      .call(d3.axisBottom(x))

    svg.append("g")
      .call(d3.axisLeft(y));
  }


  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">NUSmoney App by Anuflora Bank</h1>
          <button
            className=".btn"
            onClick={() => this.accountsButton()}
          >
            Get Accounts Data
          </button>
          <button
            className=".btn"
            onClick={() => this.customersButton()}
          >
            Get Customers Data
          </button>
          <button
            className=".btn"
            onClick={() => this.transactionsButton()}
          >
            Get Transactions Data
          </button>

        </header>


        <table className="myTable">
          <tbody>
          {this.state.accountsData.map((item) => {
            return (
              <tr key={item.id}>
                <td> {item.account} </td>
                <td> {item.balance} </td>
              </tr>
            );
          })}
          </tbody>
        </table>


        <h2> Visualisation of Data</h2>
         <div>
            <svg id="barChart"></svg>
            <svg id="barChart2"></svg>
          </div>
      </div>
    );
  }
}

export default App;
